mapper.select:
select 

from t_information

	
			contract = #{contract,jdbcType=CHAR}
		
		
			and type = #{type,jdbcType=CHAR}
		
		
			and tel = #{tel,jdbcType=CHAR}
		
		
			and bank = #{bank,jdbcType=CHAR}

mapper.update:
UPDATE t_information
SET
interest_all = #{interest_all}
,interest_month = #{interest_month}
WHERE contract = #{contract}
